    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.lids.com/nfl/buffalo-bills",
            "html" : cache.load("https://www.lids.com/nfl/buffalo-bills")
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Buffalo Bills Antigua NFL Men\'s Leader 1/4 Zip Pullover';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '70.00',
                    url: 'https://www.lids.com/buffalo-bills-antigua-nfl-mens-leader-14-zip-pullover/20985646',
                    image: 'http://lf.lids.com/hwl?set=sku[20985646],c[2],w[400],h[300]&call=url[file:product]',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Buffalo Bills Jim Kelly Mitchell & Ness NFL Replica Throwback Jersey';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '150.00',
                    url: 'https://www.lids.com/buffalo-bills-jim-kelly-mitchell-ness-nfl-replica-throwback-jersey/20527431',
                    image: 'http://lf.lids.com/hwl?set=sku[20527431],c[2],w[400],h[300]&call=url[file:product]',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

