describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "http://www.lionbrand.com/patterns",
        "html": cache.load("http://www.lionbrand.com/patterns")
    }

    // Change to be the number of products on the page
    var x = 20;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Claire Pillow';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: null,
                url: 'http://www.lionbrand.com/patterns/claire-pillow-l80225.html',
                image: 'http://cdn.lionbrand.com/media/catalog/product/cache/1/small_image/160x/9df78eab33525d08d6e5fb8d27136e95/l/8/l80225a.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Ulla Top (Crochet)';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: null,
                url: 'http://www.lionbrand.com/patterns/ulla-top-contrast-l80233.html',
                image: 'http://cdn.lionbrand.com/media/catalog/product/cache/1/small_image/160x/9df78eab33525d08d6e5fb8d27136e95/l/8/l80233.hs.hl_0153.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.lionbrand.com/patterns",
            "html": cache.load("http://www.lionbrand.com/patterns")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Patterns",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
