var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.lookfantastic.com', function() {

    before(function()  {
        this.timeout(5000);
    })

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.lookfantastic.com/kerastase-resistance-therepiste-serum-30ml/11134708.html",
            "html" : cache.load("http://www.lookfantastic.com/kerastase-resistance-therepiste-serum-30ml/11134708.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "KÃ©rastase Resistance Therapiste Serum (30ml)",
                    "price": "15.65",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://s4.thcdn.com/productimg/480/480/11134708-7184303059275649.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

// Zavvi logic and test added but no out of stock page found
    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.lookfantastic.com/dove-spa-strength-within-beauty-supplement-84-tablets/10662947.html",
            "html" : cache.load("http://www.lookfantastic.com/dove-spa-strength-within-beauty-supplement-84-tablets/10662947.html")
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Dove Spa Strength Within Beauty Supplement (84 Tablets)",
                    "price": "35.00",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://s3.thcdn.com/productimg/480/480/10662947-2034294314609498.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.lookfantastic.com/health-beauty/make-up/eyes.list",
            "html" : cache.load("http://www.lookfantastic.com/dove-spa-strength-within-beauty-supplement-84-tablets/10662947.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 66,
                    "pages": 12
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.lookfantastic.com/health-beauty/make-up/eyes.list",
            "html" : cache.load("http://www.lookfantastic.com/health-beauty/make-up/eyes.list")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Eyes"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.lookfantastic.com/health-beauty/make-up/eyes.list",
            "html" : cache.load("http://www.lookfantastic.com/health-beauty/make-up/eyes.list")
        }

        // Change to be the number of products on the page
        var x = 66;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Maybelline Eraser Eye Concealer (Various Shades)';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '7.99',
                    url: 'http://www.lookfantastic.com/maybelline-eraser-eye-concealer-various-shades/11143225.html',
                    image: 'https://s1.thcdn.com/productimg/300/300/11143225-1254307530622376.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Daniel Sandler Black Velvet Waterproof Eyeliner';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '9.25',
                    url: 'http://www.lookfantastic.com/daniel-sandler-black-velvet-waterproof-eyeliner/10553415.html',
                    image: 'https://s3.thcdn.com/productimg/300/300/10553415-1734434587171673.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
