var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.lumens.com/model-one-bluetooth-am/fm-radio-by-tivoli-audio-uu519923.html#cgid=%0A%09%0A%09%09120%0A%09%0A&&tileIndex=2"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Tivoli Audio Model One Bluetooth AM/FM Radio",
                    "price": "179.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images.lumens.com/is/image/Lumens/uu519923?$Lumens.com-350$',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.lumens.com/indoor-wall-sconces/#sz=48&start=0"
        }

        // Change to be the number of products on the page
        var x = 34;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'WAC Lighting Tube Indoor/Outdoor LED Wall Sconce';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '179.50',
                    url: 'https://www.lumens.com/tube-indoor/outdoor-led-wall-sconce-by-wac-lighting-uu504710.html',
                    image: 'https://images.lumens.com/is/image/Lumens/KCPP136239?$Lumens.com-250$',
                    country: 'us',
                    sku: 'outdoor-led-wall-sconce-by-wac-lighting-uu504710'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Nelson Bubble Lamps Cigar Bubble Wall Sconce';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '335.75',
                    url: 'https://www.lumens.com/cigar-bubble-wall-sconce-by-nelson-bubble-lamps-GNBP151447.html',
                    image: 'https://images.lumens.com/is/image/Lumens/uu458942?$Lumens.com-250$',
                    country: 'us',
                    sku: 'cigar-bubble-wall-sconce-by-nelson-bubble-lamps-GNBP151447'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.lumens.com/clocks/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.lumens.com/clocks/"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Modern Clocks"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
