var should = require('should');
var Structure = require('../lib/structure.js')
let cache = require('../lib/cache')

describe('www.macys.com', function() {

    this.timeout(5000);

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.macys.com/shop/makeup-and-perfume/eye-makeup?id=30520&cm_sp=us_hdr-_-beauty-_-30520_eyes_COL1",
            "html": cache.load("https://www.macys.com/shop/makeup-and-perfume/eye-makeup?id=30520&cm_sp=us_hdr-_-beauty-_-30520_eyes_COL1")
        }

        it('should return 18 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(18);
                done()
            })
        });

        it('last product', function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'FAO Schwarz Children\'s Construction Worker Costume',
                    price: '27.99',
                    url: 'http://www.macys.com/shop/product/fao-schwarz-childrens-construction-worker-costume?ID=4895057&CategoryID=22911',
                    image: 'https://slimages.macysassets.com/is/image/MCY/products/6/optimized/8826606_fpx.tif?bgc=255,255,255&wid=224&qlt=90,0,0&layer=comp&op_sharpen=0&resMode=bicub&op_usm=0.7,1.0,0.5,0&fmt=jpeg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product', function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Disney Frozen 26" Olaf Plush Toy',
                    price: '14.99',
                    url: 'http://www.macys.com/shop/product/disney-frozen-26-olaf-plush-toy?ID=5170994&CategoryID=22911',
                    image: 'https://slimages.macysassets.com/is/image/MCY/products/6/optimized/8953366_fpx.tif?bgc=255,255,255&wid=224&qlt=90,0,0&layer=comp&op_sharpen=0&resMode=bicub&op_usm=0.7,1.0,0.5,0&fmt=jpeg',
                    country: 'us'
                    
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.macys.com/shop/product/melissa-doug-wooden-frozen-treats-set?ID=5074917&CategoryID=22911#fn=sp%3D2%26spc%3D626%26ruleId%3D2%7CBOOST%20SAVED%20SET%26searchPass%3DmatchNone%26slotId%3D49",
            "html": cache.load("https://www.macys.com/shop/product/melissa-doug-wooden-frozen-treats-set?ID=5074917&CategoryID=22911#fn=sp%3D2%26spc%3D626%26ruleId%3D2%7CBOOST%20SAVED%20SET%26searchPass%3DmatchNone%26slotId%3D49")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Melissa and Doug Melissa & Doug Wooden Frozen Treats Set",
                    "price": "19.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://assets.macysassets.com/navapp/img/rel_interstitial_loading.gif',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should return false when product is out of stock', function(done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": null,
    //                 "price": null,
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     })

    // })

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.macys.com/shop/kids-clothes/kids-games-toys?id=22911",
            "html": cache.load("https://www.macys.com/shop/kids-clothes/kids-games-toys?id=22911")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 57,
                    "pages": 13,
                    "url" : "https://www.macys.com/shop/kids-clothes/kids-games-toys?id=22911"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.macys.com/shop/kids-clothes/kids-games-toys?id=22911",
            "html": cache.load("https://www.macys.com/shop/kids-clothes/kids-games-toys?id=22911")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Kitchen Gadgets",
                    "url" : "https://www.macys.com/shop/kids-clothes/kids-games-toys?id=22911"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Checking to see if no pagination works', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.macys.com/shop/kids-clothes/kids-games-toys?id=22911",
            "html": cache.load("https://www.macys.com/shop/kids-clothes/kids-games-toys?id=22911")
        }

        it('should return 0 items and null pages', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 0,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })


});
