var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.mamasandpapas.com/en-gb/hedgerow-activity-spiral/p/755282795/",
            "html": cache.load("https://www.mamasandpapas.com/en-gb/hedgerow-activity-spiral/p/755282795/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Hedgerow - Activity Spiral",
                    "price": "16.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://i1.adis.ws/i/mamasandpapas/755282795_HEDGEROW_ACTIVITY_SPIRAL?$productpagemainimage$',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.mamasandpapas.com/en-gb/c/travel-toys-0months/",
            "html": cache.load("https://www.mamasandpapas.com/en-gb/c/travel-toys-0months/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Travel Toys"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.mamasandpapas.com/en-gb/c/toys-gifts?q=%3AtopRated%3ApriceRange%3A%C2%A30%20-%20%C2%A320",
            "html": cache.load("https://www.mamasandpapas.com/en-gb/c/toys-gifts?q=%3AtopRated%3ApriceRange%3A%C2%A30%20-%20%C2%A320")
        }

        // Change to be the number of products on the page
        var x = 18;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hedgerow - Activity Spiral';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '16.00',
                    url: 'www.mamasandpapas.com/en-gb/hedgerow-activity-spiral/p/755282795/',
                    image: 'https://i1.adis.ws/i/mamasandpapas/755282795_HEDGEROW_ACTIVITY_SPIRAL?$rangepagegridimage$',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Welcome to the World Bunny Rattle';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '6.00',
                    url: 'www.mamasandpapas.com/en-gb/welcome-to-the-world-bunny-rattle/p/485535001/',
                    image: 'https://i1.adis.ws/i/mamasandpapas/485535001_WTTW_Rattle?$rangepagegridimage$',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
