    var should = require('should');
    var Structure = require('../lib/structure.js')
    const cache = require('../lib/cache')

    describe('supplierTest', function () {

        describe('Search Page', function () {

            var payload = {
                "pageType": "search",
                "url": "https://www.mandmdirect.com/01/gifts/audio-and-technology?tn9",
                "html": cache.load("https://www.mandmdirect.com/01/gifts/audio-and-technology?tn9")
            }
            var x = 57
            it('should return' + x + ' number of products', function () {

                const searchPage = Structure(payload)
                //     console.log(searchPage)
                searchPage.length.should.equal(x)


                var first = searchPage.shift()
                first.name.should.be.equal("Intempo Shower Speaker Black");
                first.price.should.be.equal("7.99");
                first.url.should.be.equal("http://www.mandmdirect.com/01/details/MM4200/Intempo-Shower-Speaker-Black");
                first.image.should.be.equal("https://resources.mandmdirect.com/Images/_default/m/m/4/mm4200_1_large.jpg")
                first.country.should.be.equal('uk');

                var last = searchPage.pop()

                last.name.should.be.equal("intempo Leather Effect Headphones Green");
                last.price.should.be.equal("10.99");
                last.url.should.be.equal("http://www.mandmdirect.com/01/details/MM4436/intempo-Leather-Effect-Headphones-Green");
                last.image.should.be.equal("https://resources.mandmdirect.com/Images/_default/m/m/4/mm4436_1_large.jpg")
                last.country.should.be.equal('uk');


            });

        });

        describe('In stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://www.mandmdirect.com/01/details/MM4429/Max-Boost-Fitness-Band",
                "html": cache.load("https://www.mandmdirect.com/01/details/MM4429/Max-Boost-Fitness-Band")
            }

            it('should be true when product is in stock', function () {

                const inStockPage = Structure(payload)
                // console.log(inStockPage)
                inStockPage.name.should.be.equal("Max Boost Fitness Band");
                inStockPage.price.should.be.equal("26.99");
                inStockPage.url.should.be.equal(payload.url);
                inStockPage.inStock.should.be.equal(true);
                should(inStockPage.stockLevel).be.equal(null);
                inStockPage.inStock.should.be.equal(true);
                inStockPage.image.should.be.equal('https://resources.mandmdirect.com/Images/_default/m/m/4/mm4429_1_cloudzoom.jpg');
                inStockPage.country.should.be.equal('uk');
            });

        });

    });