var should = require('should');
var Structure = require('../lib/structure.js')
let cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.melissaanddoug.com/caucasian-victorian-doll-family",
            "html": cache.load("http://www.melissaanddoug.com/caucasian-victorian-doll-family")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Victorian Doll Family - Caucasian",
                    "price": "19.99",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": "http://mediacdn.shopatron.com/media/mfg/677/product_image/thm/t300_fd538b9ba204e4e921dd07d988f99426.jpg?1467735572",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.melissaanddoug.com/search?cgid=shop-by-age-toys-for-8-years-and-older",
            "html": cache.load("http://www.melissaanddoug.com/search?cgid=shop-by-age-toys-for-8-years-and-older")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 36,
                    "pages": 21,
                    "url": "http://www.melissaanddoug.com/search?cgid=shop-by-age-toys-for-8-years-and-older"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.melissaanddoug.com/search?cgid=shop-by-age-toys-for-8-years-and-older",
            "html": cache.load("http://www.melissaanddoug.com/search?cgid=shop-by-age-toys-for-8-years-and-older")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "8 years and up",
                    "url": "http://www.melissaanddoug.com/search?cgid=shop-by-age-toys-for-8-years-and-older"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "http://www.melissaanddoug.com/our-toys/active-play-and-outdoor/bubbles/",
            "html": cache.load("http://www.melissaanddoug.com/our-toys/active-play-and-outdoor/bubbles/")
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Mombo Snake Bubbles';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '2.99',
                    url: 'https://www.melissaanddoug.com/mombo-snake-bubbles/6106.html?cgid=our-toys-active-play-and-outdoor-bubbles',
                    image: 'http://www.melissaanddoug.com/dw/image/v2/BBDH_PRD/on/demandware.static/-/Sites-master-catalog/default/dwc6982502/large/006106_1.jpg?sw=255&sh=259&sm=fit',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bollie Ladybug Bubble Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '9.99',
                    url: 'https://www.melissaanddoug.com/bollie-ladybug-bubble-set/6162.html?cgid=our-toys-active-play-and-outdoor-bubbles',
                    image: 'http://www.melissaanddoug.com/dw/image/v2/BBDH_PRD/on/demandware.static/-/Sites-master-catalog/default/dwff22281f/large/006162_1.jpg?sw=255&sh=259&sm=fit',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
