    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.menards.com/main/pet-wildlife/pet-bowls-storage/pet-food-storage/c-8335.htm",
            "html" : cache.load("http://www.menards.com/main/pet-wildlife/pet-bowls-storage/pet-food-storage/c-8335.htm"),
        }

        // Change to be the number of products on the page
        var x = 4;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(error,result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Masterpaws 2-Cup Aluminum Food Scoop';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(error,result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '4.99',
                    url: 'http://www.menards.com/main/grocery-pet/pet-bowls-storage/pet-food-storage/masterpaws-2-cup-aluminum-food-scoop/p-1444421202727-c-8335.htm?tid=-5134766338152749865',
                    image: 'http://hw.menardc.com/main/items/media/1637/ProductSmall/gino_8116.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Pet Food Can Cover';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(error,result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '1.19',
                    url: 'http://www.menards.com/main/grocery-pet/pet-bowls-storage/pet-food-storage/pet-food-can-cover/p-1444428130057-c-8335.htm?tid=-5134766338152749865',
                    image: 'http://hw.menardc.com/main/items/media/CURTI006/ProductSmall/2539005.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.menards.com/main/tools-hardware/power-tools-accessories/power-tool-combo-kits/c-9066.htm",
            "html" : cache.load("http://www.menards.com/main/tools-hardware/power-tools-accessories/power-tool-combo-kits/c-9066.htm"),

        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(error,pagination) {
                var expected = JSON.stringify({
                    "items": 20,
                    "pages": 3,
                    "url":"http://www.menards.com/main/tools-hardware/power-tools-accessories/power-tool-combo-kits/c-9066.htm"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.menards.com/main/pet-wildlife/pet-bowls-storage/pet-food-storage/c-8335.htm",
            "html" : cache.load("http://www.menards.com/main/pet-wildlife/pet-bowls-storage/pet-food-storage/c-8335.htm")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(error,category) {
                var expected = JSON.stringify({
                    "name": "Pet Food Storage",
                    "url":"http://www.menards.com/main/pet-wildlife/pet-bowls-storage/pet-food-storage/c-8335.htm"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

