var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.menkind.com', function() {

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.menkind.co.uk/superman-bath-duck",
            "html": cache.load("https://www.menkind.co.uk/superman-bath-duck")
        }

        it('should be true when product is in stock', function () {

            const inStockPage =  Structure(payload)
            inStockPage.name.should.be.equal("Superman Bath Duck");
            inStockPage.price.should.be.equal("2.49");
            inStockPage.url.should.be.equal("http://www.menkind.co.uk/superman-bath-duck");
            inStockPage.inStock.should.be.equal(false);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://www.menkind.co.uk/media/catalog/product/cache/49dcd5d85f0fa4d590e132d0368d8132/s/u/superman-bath-duck-_1_b.jpg');
            inStockPage.country.should.be.equal('uk');            
         });
 
    });

    xdescribe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.menkind.co.uk/arctic-force-snowball-blaster-solo"
        }

        it('should be true when product is in stock', function () {

            const inStockPage =  Structure(payload)
            inStockPage.name.should.be.equal("Superman Bath Duck");
            inStockPage.price.should.be.equal("2.49");
            inStockPage.url.should.be.equal("http://www.menkind.co.uk/superman-bath-duck");
            inStockPage.inStock.should.be.equal(false);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://www.menkind.co.uk/media/catalog/product/cache/49dcd5d85f0fa4d590e132d0368d8132/s/u/superman-bath-duck-_1_b.jpg');
            inStockPage.country.should.be.equal('uk');            
         });
    })

    xdescribe('Not Found Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.menkind.co.uk/acre-of-land-on-the-moon-gift-bos",
            "html": cache.load("http://www.menkind.co.uk/acre-of-land-on-the-moon-gift-bos")

        }

        it('should be true when product is in stock', function () {

            const inStockPage =  Structure(payload)
            inStockPage.name.should.be.equal("Superman Bath Duck");
            inStockPage.price.should.be.equal("2.49");
            inStockPage.url.should.be.equal("http://www.menkind.co.uk/superman-bath-duck");
            inStockPage.inStock.should.be.equal(false);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://www.menkind.co.uk/media/catalog/product/cache/49dcd5d85f0fa4d590e132d0368d8132/s/u/superman-bath-duck-_1_b.jpg');
            inStockPage.country.should.be.equal('uk');            
         });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.menkind.co.uk/home-garden/food-drink?p=2",
            "html": cache.load("https://www.menkind.co.uk/home-garden/food-drink?p=2")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Food & Drink Gifts",
                "url":"https://www.menkind.co.uk/home-garden/food-drink?p=2"
            })

            const category = Structure(payload)

            JSON.stringify(category).should.equal(expected);
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.menkind.co.uk/home-garden/food-drink?p=2",
            "html": cache.load("https://www.menkind.co.uk/home-garden/food-drink?p=2")
        }

        // Change to be the number of products on the page
        var x = 72;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Slush Puppie Machine';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '49.99',
                url: 'https://www.menkind.co.uk/slush-puppie-machine?categoryId=473&cachedResponseId=7c02014f-0fec-48f9-99d9-ebf0407d7b51&slotId=496073cd-e6ab-4048-9d88-193b20134590&categoryId=473&cachedResponseId=7c02014f-0fec-48f9-99d9-ebf0407d7b51&slotId=496073cd-e6ab-4048-9d88-193b20134590',
                image: 'https://www.menkind.co.uk/media/catalog/product/cache/c802d894880f27d700261fec3762e9a2/s/l/slush_puppie_machine_55415_15_.jpg',
                country: 'uk'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Sword Tankard';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '24.99',
                url: 'https://www.menkind.co.uk/sword-tankard?categoryId=473&cachedResponseId=7c02014f-0fec-48f9-99d9-ebf0407d7b51&slotId=496073cd-e6ab-4048-9d88-193b20134590',
                image: 'https://www.menkind.co.uk/media/catalog/product/cache/c802d894880f27d700261fec3762e9a2/s/w/sword_tankard_54396_1_.jpg',
                country: 'uk'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    });

});

