var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.midwayusa.com/product/2083363110/benjamin-marauder-bolt-action-pcp-pellet-air-rifle-matte-barrel",
            "html": cache.load("https://www.midwayusa.com/product/2083363110/benjamin-marauder-bolt-action-pcp-pellet-air-rifle-matte-barrel")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Benjamin Marauder Bolt Action PCP Pellet Air Rifle Matte Barrel",
                    "price": null,
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://media.mwstatic.com/product-images/880x660/Primary/435/435162.jpg",
                    "country": 'us',
                    
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.midwayusa.com/air-rifles/br?cid=23173",
            "html": cache.load("https://www.midwayusa.com/air-rifles/br?cid=23173")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Air Rifles",
                    "url": "https://www.midwayusa.com/air-rifles/br?cid=23173"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.midwayusa.com/air-rifles/br?cid=23173",
            "html": cache.load("https://www.midwayusa.com/air-rifles/br?cid=23173")
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Benjamin Marauder Bolt Action PCP Pellet Air Rifle Matte Barrel';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: null,
                    url: 'https://www.midwayusa.com/product/2083363110/benjamin-marauder-bolt-action-pcp-pellet-air-rifle-matte-barrel',
                    image: 'https://media.mwstatic.com/product-images/252x189/primary/435/435162.jpg',
                    country: 'us',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Benjamin Marauder Bolt Action PCP Pellet Air Rifle Matte Barrel';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '92.99',
                    url: 'https://www.midwayusa.com/product/910340/hk-mp5k-pdw-co2-air-rifle-177-caliber-bb-black',
                    image: 'https://media.mwstatic.com/product-images/252x189/primary/910/910340.jpg',
                    country: 'us',
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


