var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.mileskimball.com/buy-festive-snowman-greeting-card-set-of-20-356541?parentid=all-christmas-cards"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Festive Snowman Greeting Card - Set of 20",
                    "price": "12.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://media.mileskimball.com/images/p356541b.jpg',
                    "country": 'us',
                    "description": "Inside: May you find the simple joy of the holiday seasonThis endearing Christmas scene celebrates the wonderful simplicity of the holidays. Charming vintage Christmas lights encircle a cheery winter landscape. Your family and friends will be charmed by this delightful greeting. White card stock with satin-matte finish single folds to 5x7\"; red envelopes included. Personalization: Specify names as they are to be printed on the cards.Set of 20Save on additional sets!For non-personalized cards, simply select one of the two options below and leave the personalization area blank while ordering."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    //});

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.mileskimball.com/shop-all-christmas-cards"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 114,
                    "pages": 1
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.mileskimball.com/shop-all-christmas-cards"
        }

        // Change to be the number of products on the page
        var x = 101;
        
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Festive Snowman Greeting Card - Set of 20';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '12.99',
                    url: 'https://www.mileskimball.com/buy-festive-snowman-greeting-card-set-of-20-356541?parentid=all-christmas-cards',
                    image: 'https://media.mileskimball.com/images/p356541.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Desert Snowbirds Happy Holidays Personalized C...';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '11.99',
                    url: 'https://www.mileskimball.com/buy-desert-snowbirds-happy-holidays-personalized-christmas-card--351987?parentid=all-christmas-cards',
                    image: 'https://media.mileskimball.com/images/p351987.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.mileskimball.com/shop-all-christmas-cards"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "View All"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
