var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.miniaturemarket.com/wyrws013.html",
            "html": cache.load("https://www.miniaturemarket.com/wyrws013.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Armored Cartographers - Imperial Plaza /Ice Fortress North Double-Sided Map",
                    "price": "6.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://cdn.miniaturemarket.com/media/catalog/product/cache/1/image/9df78eab33525d08d6e5fb8d27136e95/a/a/aaacifn1.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.miniaturemarket.com/sif00020.html",
            "html": cache.load("https://www.miniaturemarket.com/sif00020.html")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.miniaturemarket.com/board-games/dice.html#/?page=2",
            "html": cache.load("https://www.miniaturemarket.com/board-games/dice.html#/?page=2")
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Star Wars Destiny: Darth Vader Dice Binder';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '11.99',
                    url: 'https://www.miniaturemarket.com/ffgsws39.html',
                    image: 'http://www.miniaturemarket.com/media/catalog/product/cache/1/image/145x145/9df78eab33525d08d6e5fb8d27136e95/f/f/ffgsws39.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Rayguns & Rocketships';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '51.79',
                    url: 'https://www.miniaturemarket.com/idw01080.html',
                    image: 'http://www.miniaturemarket.com/media/catalog/product/cache/1/image/145x145/9df78eab33525d08d6e5fb8d27136e95/i/d/idw01080_1.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    //    describe('Search page pagination', function () {

    //        var payload = {
    //            "pageType": "pagination",
    //            "url": "http://www.miniaturemarket.com/collectible-miniatures/star-wars-miniatures.html",
    //            "html": cache.load("http://www.miniaturemarket.com/collectible-miniatures/star-wars-miniatures.html")
    //        }

    //        it('should be true when number of items and pages correct', function (done) {

    //            Structure(payload, function (err, pagination) {
    //                var expected = JSON.stringify({
    //                    "items": 24,
    //                    "pages": 41,
    //                    "html": cache.load("http://www.miniaturemarket.com/collectible-miniatures/star-wars-miniatures.html")
    //                })
    //                JSON.stringify(pagination).should.equal(expected);
    //                done()
    //            })
    //        });

    //    });

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.miniaturemarket.com/collectible-miniatures/star-wars-miniatures.html",
            "html": cache.load("http://www.miniaturemarket.com/collectible-miniatures/star-wars-miniatures.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Star Wars",
                    "url": "http://www.miniaturemarket.com/collectible-miniatures/star-wars-miniatures.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});