    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.miniinthebox.com/iphone-generic-accessories_c9362"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,// can put the number of products on one page here//
                    "pages": 26 // the number of pages goes here (not the number of products times the number of pages) // 
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.miniinthebox.com/iphone-generic-accessories_c9362"
        }

        // Change to be the number of products on the page
        var x = 60;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Lityus MFI Certified USB Cable 3.28ft (1M) for iPhone 7 6s 6 Plus SE 5s 5c 5, iPad Data Charger Cable';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '5.84',
                    url: 'http://www.miniinthebox.com/lityus-apple-mfi-certified-usb-cable-3-28ft-1m-for-iphone-6-6s-6-6s-plus-iphone-5-5s-5c-ipad-data-charger-cable_p4774113.html?category_id=9362&prm=2.2.1.1',
                    image: 'http://miniimg1.rightinthebox.com/images/190x190/201608/ptog1471828959651.jpg',
                    country: 'us'
                    // Can you make sure to fill in the country (us if $, uk if Â£)//
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Kanen IP-809 Sports Headset Call Headphones Laptop Card Can Phone Headset Sports Headphones';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '4.59',
                    url: 'http://www.miniinthebox.com/kanen-ip-809-sports-headset-call-headphones-laptop-card-can-phone-headset-sports-headphones_p4788732.html?category_id=9362&prm=2.2.1.1',
                    image: 'http://miniimg4.rightinthebox.com/images/190x190/201604/ppov1460705954844.jpg',
                    country: 'us'
                    //Add country here as well please//
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.miniinthebox.com/iphone-generic-accessories_c9362"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "iPhone Universal Accessories"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

