    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.minimodelshop.co.uk/model-aircraft/Lockheed"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Lockheed Model Aircraft"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.minimodelshop.co.uk/model-aircraft/Lockheed"
        }

        // Change to be the number of products on the page
        var x = 33;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Lockheed C-130 J-30 Super Hercules (USAF) [Kit]';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '39.99',
                    url: 'http://www.minimodelshop.co.uk/diecast-model-aircraft/lockheed-c-130-j-30-super-hercules-plastic-model-airplane-kit-minicraft-models-min14700',
                    image: 'http://www.minimodelshop.co.uk/picture/MinicraftModels-MIN14700/lockheed-c-130-j-30-super-hercules-plastic-model-airplane-kit-minicraft-models-min14700-p.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Lockheed Vega Shell Oil [Kit]';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '32.99',
                    url: 'http://www.minimodelshop.co.uk/diecast-model-aircraft/lockheed-vega-shell-oil-plastic-model-kit-amt-amt950',
                    image: 'http://www.minimodelshop.co.uk/picture/AMT-AMT950/lockheed-vega-shell-oil-plastic-model-kit-amt-amt950-p.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

