    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.moosejaw.com/moosejaw/shop/search_Audio-Equipment____"
        }

        // Change to be the number of products on the page
        var x = 26;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Outdoor Tech Privates Wireless Touch Control Headphones';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '50.99',
                    url: 'http://www.moosejaw.com/moosejaw/shop/product_Outdoor-Tech-Privates-Wireless-Touch-Control-Headphones_10251169_10208_10000001_-1_',
                    image: 'http://s7d1.scene7.com/is/image/MoosejawMB/10251169x1037830_zm?$thumb225$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Thule Vectros 15IN MacBook Pro Retina Bumper';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '79.95',
                    url: 'http://www.moosejaw.com/moosejaw/shop/product_Thule-Vectros-15IN-MacBook-Pro-Retina-Bumper_10290873_10208_10000001_-1_',
                    image: 'http://s7d1.scene7.com/is/image/MoosejawMB/10290873x1126126_zm?$thumb225$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
         describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.moosejaw.com/moosejaw/shop/search_Climbing-Backpacks____"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.moosejaw.com/moosejaw/shop/search_Audio-Equipment____"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Audio Equipment"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

