var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.morphyrichards.co.uk/products/toasters/2-slice-toasters/44206-Accents-Red-2-Slice-Toaster.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Accents Red 2 Slice Toaster - 44206",
                    "price": "26.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'www.morphyrichards.co.uk/Images/Products/Default/large/HR44206.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.morphyrichards.co.uk/products/toasters/2-slice-toasters/221003-Chroma-White-Plastic-2-Slice-Toaster.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Chroma White Plastic 2 Slice Toaster - 221003",
                    "price": "24.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "www.morphyrichards.co.uk/Images/Products/Default/large/HR221003.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.morphyrichards.co.uk/products/toasters/2-slice-toasters/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "2 Slice Toasters"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.morphyrichards.co.uk/products/toasters/2-slice-toasters/"
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Chroma White Plastic 2 Slice Toaster - 221003';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '24.99',
                    url: 'www.morphyrichards.co.uk/products/toasters/2-slice-toasters/221003-Chroma-White-Plastic-2-Slice-Toaster.html',
                    image: 'www.morphyrichards.co.uk/Images/Products/Default/medium/HR221003.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Chroma White Plastic 2 Slice Toaster - 221151';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '39.99',
                    url: 'www.morphyrichards.co.uk/products/toasters/2-slice-toasters/221151-Chroma-White-Plastic-2-Slice-Toaster.html',
                    image: 'www.morphyrichards.co.uk/Images/Products/Default/medium/HR221151.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
