var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.mscdirect.com/product/details/84425321"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Parker - 1/4 Inch OD, Nickel Plated Brass, Push to Connect Union Tee",
                    "price": "6.80",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn.mscdirect.com/global/images/ProductImages/8442532-23.jpg',
                    "country": 'us',
                    "description": 'Specs Type Union Tee Tube Outside Diameter (Inch) 1/4 Maximum Working Pressure 300.00 Connection Type Tube to Tube Material Nickel Plated Brass O-Ring Material Nitrile View the Safety Data Sheet (SDS).'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.mscdirect.com/product/details/47041769"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "MakerBot - MP06325 - 3D Printer Accessories Type: Extruder For Use With: Replicator (5th Generation); Replicator Mini",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://cdn.mscdirect.com/global/images/ProductImages/4704176-21.jpg',
                    "country": 'us',
                    "description": 'Specs 3D Printer Accessories; Type: Extruder; For Use With: Replicator (5th Generation); Replicator Mini Type Extruder For Use With Replicator (5th Generation); Replicator Mini'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.mscdirect.com/browse/tn/Hose-Tube-Fittings-Valves/Metal-Push-To-Connect-Tube-Fittings?navid=4287924576&page=0&_=1481012651435&searchterm=tee"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 25,
                    "pages": 23
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.mscdirect.com/browse/tn/Holemaking/Jobber-Length-Drill-Bits?navid=4287924545+4287237458&mc=0837934"
        }

        // Change to be the number of products on the page
        var x = 50;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Guhring - Jobber Length Drill Bits Drill Bit Size (Wire): #7 Drill Bit Size (Decimal Inch): 0.2012';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '119.23',
                    url: 'http://www.mscdirect.com/product/details/41443623',
                    image: 'http://cdn.mscdirect.com/global/images/ProductImages/4144342-21.gif',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Alpha Technologies - 3/8 Inch OD, Nickel Plated Brass, Push to Connect Union Tee';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '11.56',
                    url: 'http://www.mscdirect.com/product/details/82916685',
                    image: 'http://cdn.mscdirect.com/global/images/ProductImages/8291665-11.gif',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.mscdirect.com/browse/tn/Hose-Tube-Fittings-Valves/Metal-Push-To-Connect-Tube-Fittings?navid=4287924576&page=0&_=1481012651435&searchterm=tee"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Metal Push-To-Connect Tube Fittings"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
