var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.myotcstore.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.myotcstore.com/store/p/63655-Barn-Dad-Nutrition-Real-Clean-Chicken-And-Egg-White-Protein-Chocolate-1-98-Lb",
            "html": cache.load("https://www.myotcstore.com/store/p/63655-Barn-Dad-Nutrition-Real-Clean-Chicken-And-Egg-White-Protein-Chocolate-1-98-Lb")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Barn Dad Nutrition Real Clean Chicken And Egg White Protein Chocolate, 1.98 Lb",
                    "price": "39.52",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.myotcstore.com/resize/images/k-productimages/Kpp/AXSN2210557.jpg?bw=500&w=500&bh=500&h=500",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.myotcstore.com/store/p/45694-Eos-Everyday-Fast-Absorbing-Hand-Lotion-Fresh-Flowers-1-5-Oz",
            "html": cache.load("https://www.myotcstore.com/store/p/45694-Eos-Everyday-Fast-Absorbing-Hand-Lotion-Fresh-Flowers-1-5-Oz")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Eos Everyday Fast-Absorbing Hand Lotion, Fresh Flowers - 1.5 Oz",
                    "price": "1.81",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.myotcstore.com/resize/images/E-productimages/Eos/Flowers_272245.jpg?bw=500&w=500&bh=500&h=500",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.myotcstore.com/store/c/2529/Protein-Supplements",
        "html": cache.load("https://www.myotcstore.com/store/c/2529/Protein-Supplements")
    }

    // Change to be the number of products on the page
    var x = 36;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = '21St Century Therapeutic-M Tablets - 130 Ea';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '6.20',
                url: 'https://www.myotcstore.com/store/p/42038-21St-Century-Therapeutic-M-Tablets-130-Ea',
                image: 'https://www.myotcstore.com/resize/images/Numberimages/21stCentury/gauntry_910737.jpg?bw=250&bh=250',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Growing Naturals Raw Pea Protein Powder, Original, 32.2 oz';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '24.62',
                url: 'https://www.myotcstore.com/store/p/57174-Growing-Naturals-Raw-Pea-Protein-Powder-Original-32-2-oz',
                image: 'https://www.myotcstore.com/resize/images/g-productimages/GrowingNaturals/AXTEWJ0002-SN1265503.jpg?bw=250&bh=250',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.myotcstore.com/store/c/2529/Protein-Supplements",
            "html": cache.load("https://www.myotcstore.com/store/c/2529/Protein-Supplements")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Protein Supplements",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
