var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.mytoolshed.co.uk/wera-red-bull-racing-kraftform-kompakt-interchange-s-driver-18pc.html",
            "html": cache.load("https://www.mytoolshed.co.uk/wera-red-bull-racing-kraftform-kompakt-interchange-s-driver-18pc.html")
        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)       
            console.log(inStockPage)     
            inStockPage.name.should.be.equal("Wera Red Bull Racing Kraftform Kompakt Interchange S/Driver 18pc");
            inStockPage.price.should.be.equal("59.99");
            inStockPage.url.should.be.equal("https://www.mytoolshed.co.uk/wera-red-bull-racing-kraftform-kompakt-interchange-s-driver-18pc.html");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://images.mytoolshed.co.uk/catalog/product/cache/image/700x560/e9c3970ab036de70892d86c6d221abfe/w/e/wer227703_mtsm_1.jpg');
            inStockPage.country.should.be.equal('uk');            
         });
    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        
        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)            
            inStockPage.name.should.be.equal("");
            inStockPage.price.should.be.equal("");
            inStockPage.url.should.be.equal("");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('');
            inStockPage.country.should.be.equal('');            
         });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.mytoolshed.co.uk/power-tool-accessories/jigsaw-blades.html",
            "html": cache.load("https://www.mytoolshed.co.uk/power-tool-accessories/jigsaw-blades.html")
        }
        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Jigsaw & Sabre Saw Blades",
                "url":"https://www.mytoolshed.co.uk/power-tool-accessories/jigsaw-blades.html"
            })

            const category = Structure(payload)

            JSON.stringify(category).should.equal(expected);
        });        



    });

    describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.mytoolshed.co.uk/power-tool-accessories/jigsaw-blades/jigsaw-blade-set.html",
                    "html": cache.load("https://www.mytoolshed.co.uk/power-tool-accessories/jigsaw-blades.html")
                }
                it('should be true when number of items and pages correct', function () {

                    var expected = JSON.stringify({
                        "items": "",
                        "pages": 1,
                        "url":"https://www.mytoolshed.co.uk/power-tool-accessories/jigsaw-blades/jigsaw-blade-set.html"
                    })
        
                    const pagination = Structure(payload)
        
                    JSON.stringify(pagination).should.equal(expected);
                });
        
            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.mytoolshed.co.uk/power-tool-accessories/jigsaw-blades/jigsaw-blade-set.html",
            "html": cache.load("https://www.mytoolshed.co.uk/power-tool-accessories/jigsaw-blades/jigsaw-blade-set.html")
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Irwin Assorted Jigsaw Blades (Pack 10)';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '6.95',
                url: 'https://www.mytoolshed.co.uk/irwin-assorted-jigsaw-blades-pack-10.html',
                image: 'https://images.mytoolshed.co.uk/catalog/product/cache/small_image/170x180/beff4985b56e3afdbeabfc89641a4582/i/r/irw10505817---l.jpg',
                country: 'uk'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Ryobi RAK-20JB Assorted Jigsaw Blades (Pack 20)';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '21.64',
                url: 'https://www.mytoolshed.co.uk/ryobi-rak-20jb-assorted-jigsaw-blades-pack-20.html',
                image: 'https://images.mytoolshed.co.uk/catalog/product/cache/small_image/170x180/beff4985b56e3afdbeabfc89641a4582/r/y/rybrak20jb.jpg',
                country: 'uk'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 


});


