var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.newark.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.newark.com/schneider-electric/lr9d08kitd09g7/tesys-kit-contactor-over-load/dp/94Y9279",
            "html" : cache.load("http://www.newark.com/schneider-electric/lr9d08kitd09g7/tesys-kit-contactor-over-load/dp/94Y9279")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "SCHNEIDER ELECTRIC LR9D08KITD09G7 TESYS KIT, CONTACTOR & OVER LOAD RELAY",
                    "price": "105.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.newark.com/productimages/standard/en_US/94Y9274-40.jpg',
                    "country": 'us',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.newark.com/dialight/586-2402-205f/led-bulb-ba9s-green-t-3-1-4/dp/20M6818",
            "html" : cache.load("http://www.newark.com/dialight/586-2402-205f/led-bulb-ba9s-green-t-3-1-4/dp/20M6818")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "DIALIGHT 586-2402-205F LED Replacement Lamp, Single-Chip, BA9s, Green, T-3 1/4 (10mm), 525 nm, 825 mcd",
                    "price": "4.70",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.newark.com/productimages/standard/en_US/20M6818-40.jpg',
                    "country": 'us',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.newark.com/webapp/wcs/stores/servlet/Search?catalogId=15003&langId=-1&storeId=10194&categoryId=800000005102&eq=N%3D201845%2B422%2B2203%26amp%3BNs%3DP_STORE_MARKETING_RANK_NEWARK_US%257c0%257c%257cP_MAN_PART_NUM%257c0%26amp%3BNtpc%3D1%26amp%3BNtpr%3D1&pageSize=100&beginIndex=1&showResults=true&aa=true&vw=",
            "html" : cache.load("http://www.newark.com/webapp/wcs/stores/servlet/Search?catalogId=15003&langId=-1&storeId=10194&categoryId=800000005102&eq=N%3D201845%2B422%2B2203%26amp%3BNs%3DP_STORE_MARKETING_RANK_NEWARK_US%257c0%257c%257cP_MAN_PART_NUM%257c0%26amp%3BNtpc%3D1%26amp%3BNtpr%3D1&pageSize=100&beginIndex=1&showResults=true&aa=true&vw=")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 100,
                    "pages": 15,
                    "url" : "http://www.newark.com/webapp/wcs/stores/servlet/Search?catalogId=15003&langId=-1&storeId=10194&categoryId=800000005102&eq=N%3D201845%2B422%2B2203%26amp%3BNs%3DP_STORE_MARKETING_RANK_NEWARK_US%257c0%257c%257cP_MAN_PART_NUM%257c0%26amp%3BNtpc%3D1%26amp%3BNtpr%3D1&pageSize=100&beginIndex=1&showResults=true&aa=true&vw="
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.newark.com/c/semiconductors-ics/amplifiers-comparators/audio-amplifiers",
            "html" : cache.load("http://www.newark.com/c/semiconductors-ics/amplifiers-comparators/audio-amplifiers")
        }

        // Change to be the number of products on the page
        var x = 13;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'TEXAS INSTRUMENTS - Audio Power Amplifier';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1.39',
                    url: 'http://www.newark.com/texas-instruments/tpa6130a2rtjr/audio-power-amplifier-class-ab/dp/34M0132',
                    image: 'http://www.newark.com/productimages/promo/en_US/34M0132-40.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'TEXAS INSTRUMENTS - Audio Power Amplifier';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '1.56',
                    url: 'http://www.newark.com/texas-instruments/lm4871m-nopb/audio-power-amplifier-class-ab/dp/41K5780',
                    image: 'http://www.newark.com/productimages/promo/en_US/41K5780-40.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
      describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.newark.com/webapp/wcs/stores/servlet/Search?catalogId=15003&langId=-1&storeId=10194&categoryId=800000005102&eq=N%3D201845%2B422%2B2203%26amp%3BNs%3DP_STORE_MARKETING_RANK_NEWARK_US%257c0%257c%257cP_MAN_PART_NUM%257c0%26amp%3BNtpc%3D1%26amp%3BNtpr%3D1&pageSize=100&beginIndex=1&showResults=true&aa=true&vw=",
            "html" : cache.load("http://www.newark.com/webapp/wcs/stores/servlet/Search?catalogId=15003&langId=-1&storeId=10194&categoryId=800000005102&eq=N%3D201845%2B422%2B2203%26amp%3BNs%3DP_STORE_MARKETING_RANK_NEWARK_US%257c0%257c%257cP_MAN_PART_NUM%257c0%26amp%3BNtpc%3D1%26amp%3BNtpr%3D1&pageSize=100&beginIndex=1&showResults=true&aa=true&vw=")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Motors & Drives",
                    "url" : "http://www.newark.com/webapp/wcs/stores/servlet/Search?catalogId=15003&langId=-1&storeId=10194&categoryId=800000005102&eq=N%3D201845%2B422%2B2203%26amp%3BNs%3DP_STORE_MARKETING_RANK_NEWARK_US%257c0%257c%257cP_MAN_PART_NUM%257c0%26amp%3BNtpc%3D1%26amp%3BNtpr%3D1&pageSize=100&beginIndex=1&showResults=true&aa=true&vw="
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
