var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.newegg.com/Product/Product.aspx?Item=N82E16820147373&cm_re=samsung-_-20-147-373-_-Product"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": 'SAMSUNG 850 EVO 2.5" 500GB SATA III 3-D Vertical Internal Solid State Drive (SSD) MZ-75E500B/AM',
                    "price": "164.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images10.newegg.com/NeweggImage/ProductImage/20-147-373-09.jpg',
                    "country": 'us',
                    "description": 'Samsung has designed the 850 EVO in multiple form factors with compatibility in mind. The 2.5-inch size is designed to fit most desktop PCs and laptops, while the SATA-based M.2 and mSATA are ideal for ultra-slim mobile computing.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.newegg.com/Product/Product.aspx?Item=N82E16820147595&cm_re=samsung-_-20-147-595-_-Product"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "SAMSUNG 960 EVO M.2 1TB NVMe PCI-Express 3.0 x4 Internal Solid State Drive (SSD) MZ-V6E1T0BW",
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://images10.newegg.com/ProductImage/20-147-595-01.jpg',
                    "country": 'us',
                    "description": null
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.newegg.com/Product/ProductList.aspx?Submit=ENE&N=-1&IsNodeId=1&Description=samsung&page=10&bop=And&PageSize=96&order=BESTMATCH"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 19
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.newegg.com/PS4-Video-Games/SubCategory/ID-3141"
        }

        // Change to be the number of products on the page
        var x = 37;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'The Elder Scrolls Online: Morrowind - Collector\'s Edition - PlayStation 4';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '99.99',
                    url: 'https://www.newegg.com/Product/Product.aspx?Item=N82E16879228071',
                    image: 'https://images10.newegg.com/NeweggImage/ProductImageCompressAll300/79-228-071-V06.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Batman: Arkham VR - PlayStation 4';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '19.99',
                    url: 'https://www.newegg.com/Product/Product.aspx?Item=N82E16879333190',
                    image: 'https://images10.newegg.com/ProductImageCompressAll300/79-333-190-02.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.newegg.com/Processors-Desktops/SubCategory/ID-343"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Processors - Desktops"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


});
