var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.neweggbusiness.com', function() {

    this.timeout(5000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.neweggbusiness.com/product/product.aspx?item=9b-96-110-013"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Cuisinart CPT-180 White Metal Classic 4-Slice Toaster",
                    "price": "69.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://a248.e.akamai.net/f/248/9241/30d/images1.newegg.com/NeweggImage/ProductImageCompressAll300/96-110-013-02.jpg",
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.neweggbusiness.com/Product/Product.aspx?Item=9B-96-759-006&nm_mc=afc-cjb2b&cm_mmc=afc-cjb2b-_-Hair+Dryers-_-Revlon-_-9B96759006"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "REVLON RV484 Ion 1875-Watt Hair Dryer",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://a248.e.akamai.net/f/248/9241/30d/images1.newegg.com/NeweggImage/ProductImageCompressAll300/96-759-006-01.jpg",
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })
    })

    describe('Refurbished Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.neweggbusiness.com/Product/Product.aspx?Item=9B-96-110-601&nm_mc=afc-cjb2b&cm_mmc=afc-cjb2b-_-Toasters-_-Cuisinart-_-9B96110601"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Refurbished: Cuisinart CPT-140BKFR Black Compact 4-Slice Toaster",
                    "price": "25.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://a248.e.akamai.net/f/248/9241/30d/images1.newegg.com/NeweggImage/ProductImageCompressAll300/96-110-601-03.jpg",
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })
    })

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.neweggbusiness.com/s/headphones-accessories/id-70"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 37,
                    "pages": 194
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.neweggbusiness.com/s/headphones-accessories/id-70"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Headphones & Accessories"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

   describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.neweggbusiness.com/s/headphones-accessories/id-70"
        }

        // Change to be the number of products on the page
        var x = 35;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Sennheiser Black/Silver HD 201 3.5mm/ 6.3mm Connector Around-Ear Hi-fi Headphone';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '30.99',
                    url: 'https://www.neweggbusiness.com/product/product.aspx?item=9b-26-106-455',
                    image: 'https://images1.newegg.com/NeweggImage/ProductImageCompressAll300/26-106-455-07.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Beats Executive - Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '207.48',
                    url: 'https://www.neweggbusiness.com/product/product.aspx?item=9b-0th-00zd-00025',
                    image: 'https://images1.newegg.com/ProductImageCompressAll300/A1K5_130488873318990000pb577QzlGM.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });


   });

