var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.nflshop.com/Cleveland_Browns_Women/Womens_Cleveland_Browns_Pro_Line_Heathered_Gray_Timeless_Lounge_Script_Tri-Blend_Pullover_Hoodie"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Women's Cleveland Browns Pro Line Heathered Gray Timeless Lounge Script Tri-Blend Pullover Hoodie",
                    "price": "64.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://nflshop.frgimages.com/FFImage/thumb.aspx?i=/productImages%2f_2311000%2fff_2311681a_full.jpg&w=600',
                    "country": 'us',
                    "description": "This Pro Line Timeless Lounge Script tri-blend pullover hoodie is a great way to keep the Cleveland Browns close to your heart throughout a busy day. It's the perfect sporty-chic piece to add to any ensemble with its throwback Cleveland Browns graphics and contrast color raglan sleeves."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.nflshop.com/catalog/product/Nike_Seattle_Seahawks_Flynn_Knows_T-Shirt_-_Navy_Blue"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Nike Seattle Seahawks Flynn Knows T-Shirt - Navy Blue",
                    "price": "15.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://nflshop.frgimages.com/FFImage/thumb.aspx?i=/productImages%2f_847000%2fff_847870_xl.jpg&w=600',
                    "country": 'us',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.nflshop.com/Cleveland_Browns_Women/pg/1/ps/84/so/no_sort"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 84,
                    "pages": 9
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.nflshop.com/arizona-cardinals/collectibles/t-81262364+d-2393663337+z-8-3699621921",
            "html": cache.load("https://www.nflshop.com/arizona-cardinals/collectibles/t-81262364+d-2393663337+z-8-3699621921")
        }

        // Change to be the number of products on the page
        var x = 72;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Arizona Cardinals Fidget Cube';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '5.00',
                    url: 'https://www.nflshop.com/arizona-cardinals/arizona-cardinals-fidget-cube/t-58600142+p-469791757687+z-8-3244157121',
                    image: 'https://images.footballfanatics.com/FFImage/thumb.aspx?i=/productimages/_2828000/ff_2828931_full.jpg&w=340',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Autographed Arizona Cardinals Kurt Warner Fanatics Authentic Riddell Replica Helmet with "HOF 17" Inscription';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '449.99',
                    url: 'https://www.nflshop.com/arizona-cardinals/autographed-arizona-cardinals-kurt-warner-fanatics-authentic-riddell-replica-helmet-with-hof-17-inscription/t-14712308+p-9185090025167+z-9-1878563581',
                    image: 'https://images.footballfanatics.com/FFImage/thumb.aspx?i=/productimages/_2790000/ff_2790356_full.jpg&w=340',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.nflshop.com/Cleveland_Browns_Women/pg/1/ps/84/so/no_sort"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Cleveland Browns Women"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
