    var should = require('should');
    var Structure = require('../lib/structure.js')
    const cache = require('../lib/cache')

    describe('supplierTest', function () {

        describe('Search Page', function () {

            var payload = {
                "pageType": "search",
                "url": "https://www.northerntool.com/shop/tools/category_fuel-transfer-lubrication+oil-pumps+air-operated-oil-pumps",
                "html": cache.load("https://www.northerntool.com/shop/tools/category_fuel-transfer-lubrication+oil-pumps+air-operated-oil-pumps")
            }
            var x = 48
            it('should return' + x + ' number of products', function () {

                const searchPage = Structure(payload)
                searchPage.length.should.equal(x)


                var first = searchPage.shift()
                first.name.should.be.equal("Ironton Air-Operated 5:1 Oil Pump Kit — With Cart and Hose Reel, 3.7 GPM");
                first.price.should.be.equal("559.99");
                first.url.should.be.equal("https://www.northerntool.com/shop/tools/product_200697036_200697036");
                first.image.should.be.equal("http://www.northerntool.com/images/product/200x200/582/58283_200x200.jpg")
                first.country.should.be.equal('us');

                var last = searchPage.pop()

                last.name.should.be.equal("ARO Air-Operated Double Diaphragm Oil Pump — 1/2in. Ports, 12 GPM, Aluminum/Nitrile, Model# PD05R-AAS-PGG-B");
                last.price.should.be.equal("619.99");
                last.url.should.be.equal("https://www.northerntool.com/shop/tools/product_200680293_200680293");
                last.image.should.be.equal("http://www.northerntool.com/images/product/200x200/597/59739_200x200.jpg")
                last.country.should.be.equal('us');


            });

        });

        describe('In stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://www.northerntool.com/shop/tools/product_200697036_200697036",
                "html": cache.load("https://www.northerntool.com/shop/tools/product_200697036_200697036")
            }

            it('should be true when product is in stock', function () {

                const inStockPage = Structure(payload)
                // console.log(inStockPage)
                inStockPage.name.should.be.equal("Ironton Air-Operated 5:1 Oil Pump Kit — With Cart and Hose Reel, 3.7 GPM");
                inStockPage.price.should.be.equal("559.99");
                inStockPage.url.should.be.equal(payload.url);
                inStockPage.inStock.should.be.equal(true);
                should(inStockPage.stockLevel).be.equal(null);
                inStockPage.inStock.should.be.equal(true);
                inStockPage.image.should.be.equal('http://www.northerntool.com/images/product/400x400/582/58283_400x400.jpg');
                inStockPage.country.should.be.equal('us');
            });

        });

        describe('Out Of Stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://www.northerntool.com/shop/tools/product_200685590_200685590",
                "html": cache.load("https://www.northerntool.com/shop/tools/product_200685590_200685590")
            }

            it('should return false when product is out of stock', function () {

                const inStockPage = Structure(payload)
                inStockPage.name.should.be.equal("Ingersoll Rand ARO 3:1 Air-Operated Oil Pump — 450 PSI, Model# LM2203A-31-C");
                inStockPage.price.should.be.equal('24.99');
                inStockPage.url.should.be.equal(payload.url);
                should(inStockPage.stockLevel).be.equal(null);
         //       inStockPage.inStock.should.be.equal(false);
                inStockPage.image.should.be.equal('http://www.northerntool.com/images/product/400x400/603/60398_400x400.jpg');
                inStockPage.country.should.be.equal('us')
            });

        });

    });