var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.nothingbutsavings.com', function() {

    before(function()  {
        this.timeout(5000);
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.nothingbutsavings.com/Category/8-PC-Games"
        }

        it('should return 24 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(24);
                done()
            })
        });

        it('last product should be a Assassin\'s Creed Rogue - Windows PC', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Assassin\'s Creed Rogue - Windows PC',
                    price: '29.84',
                    url: 'http://www.nothingbutsavings.com/Product/329649-Assassins-Creed-Rogue-Windows-PC',
                    image: 'http://cdn.productinformation.net/images/products/9090/329649.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be a 3 Games for Girls', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: '3 Great Games for Girls (Nancy Drew, Syberia & Saddle Up)',
                    price: '9.99',
                    url: 'http://www.nothingbutsavings.com/Product/89570-3-Great-Games-for-Girls-Nancy-Drew-Syberia-Saddle-Up',
                    image: 'http://cdn.productinformation.net/images/products/9090/89570.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.nothingbutsavings.com/Product/43014-Arthurs-Knights-Tales-of-Chivalry-for-Windows-PC"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Arthur's Knights: Tales of Chivalry for Windows PC",
                    "price": "1.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://cdn.productinformation.net/images/products/43014.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should return false when product is out of stock', function(done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     })

    // })

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.nothingbutsavings.com/Category/27-Playstation-4/1/96/AllBrands/AllPlatforms/0"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.nothingbutsavings.com/Category/8-PC-Games"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "PC Games"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


});
