var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.novatech.co.uk', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.novatech.co.uk/products/gigabyte-ga-h110m-h-intel-h110-socket-1151-micro-atx-motherboard/ga-h110m-h.html",
            "html": cache.load("https://www.novatech.co.uk/products/gigabyte-ga-h110m-h-intel-h110-socket-1151-micro-atx-motherboard/ga-h110m-h.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "GIGABYTE GA-H110M-H Intel H110 (Socket 1151) Micro ATX Motherboard",
                    "price": "53.89",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://images.novatech.co.uk/gigabyte-ga-h110m-h.jpg',
                    "country": 'uk',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.novatech.co.uk/products/hp-8-tb-3-5-inch-internal-hard-drive-sas-7200-1-pack/793703-b21.html",
            "html": cache.load('https://www.novatech.co.uk/products/hp-8-tb-3-5-inch-internal-hard-drive-sas-7200-1-pack/793703-b21.html')
        }

        it('should be true when product is out of stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "HP 8 TB 3.5\" Internal Hard Drive - SAS - 7200 - 1 Pack",
                    "price": "1296.12",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://content.etilize.com/images/300/1031543348.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.novatech.co.uk/products/components/motherboards/intelmotherboards/",
            "html": cache.load("https://www.novatech.co.uk/products/components/motherboards/intelmotherboards/")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Intel Motherboard",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.novatech.co.uk/products/cables/",
            "html": cache.load("https://www.novatech.co.uk/products/cables/")
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Power Cable - 1.8m (Kettle Lead)';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '4.99',
                    url: 'https://www.novatech.co.uk/products/power-cable-1-8m-kettle-lead/rb-250.html',
                    image: 'https://images.novatech.co.uk/pics/300/novatech-rb-250.jpg',
                    country: 'uk',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'SATA Cable - 45cm';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '2.83',
                    url: 'https://www.novatech.co.uk/products/sata-cable-45cm/88rb-404.html',
                    image: 'https://images.novatech.co.uk/pics/300/novatech-rb-250.jpg',
                    country: 'uk',
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
