var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.ocado.com/webshop/product/Ty-Pocket-Penguin-Teeny-Ty-3-/314139011?from=shop&tags=%7C20000%7C21834%7C53140&parentContainer=%7C21834%7C53140_SHELFVIEW"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Ty Pocket Penguin Teeny Ty 3 +",
                    "price": "2.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'www.ocado.com/productImages/314/314139011_0_170x170.jpg?identifier=8fdf78c6ebabae430d74bef95a4121f6',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.ocado.com/webshop/getCategories.do?tags=%7C20000%7C21834%7C53140&viewAllProducts=true&Asidebar=2"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Soft Toys"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.ocado.com/webshop/getCategories.do?tags=%7C25189"
        }

        // Change to be the number of products on the page
        var x = 400;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Roberts Bakery White Farmhouse Thick Sliced Loaf 800g';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1.60',
                    url: 'https://www.ocado.com/webshop/product/Roberts-Bakery-White-Farmhouse-Thick-Sliced-Loaf/78269011',
                    image: 'https://www.ocado.com/productImages/782/78269011_0_150x150.jpg?identifier=6d1e8ebd14780e7856c9fb1906ec961c',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Twirlywoos Run-along Fun Sounds Toodloo';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '19.99',
                    url: 'www.ocado.com/webshop/product/Twirlywoos-Runalong-Fun-Sounds-Toodloo/321525011?prevPageIndex=10&from=shop&tags=%7C20000%7C21834%7C53140&parentContainer=%7C21834%7C53140_SHELFVIEW',
                    image: 'https://content1.ocado.com/productImages/321/321525011_0_150x150.jpg?identifier=8fcfdb05e347e520923fa4a37452314e',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
