    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.officedepot.com/a/browse/mice/N=5+509565&recordsPerPageNumber=24&No=24/",
            "html": cache.load("https://www.officedepot.com/a/browse/mice/N=5+509565&recordsPerPageNumber=24&No=24/")
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Office Depot® Brand Thermal Paper Rolls, 2 1/4" x 85\', White, Pack Of 9';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '17.49',
                    url: 'http://www.officedepot.com/catalog/catalogSku.do?id=109317&SEO=Office-Depot-Brand-Thermal-Paper-Rolls__&pr=',
                    image: 'http://s7d1.scene7.com/is/image/officedepot/109317_p_1_080316?$OD%2DSearch$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'uAccept™ POS Thermal Paper, 3 1/8"W x 220\'L, 1-Ply, White, Pack Of 50';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '129.99',
                    url: 'http://www.officedepot.com/catalog/catalogSku.do?id=523623&SEO=uAccept-POS-Thermal-Paper-3-18__&pr=',
                    image: 'http://s7d1.scene7.com/is/image/officedepot/523623_p_center_ma850_300dpi?$OD%2DSearch$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

