var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.officestationery.co.uk', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.officestationery.co.uk/office-supplies/exhibition-and-conference/projectors-ohp/product/113487/",
            "html" : cache.load("https://www.officestationery.co.uk/office-supplies/exhibition-and-conference/projectors-ohp/product/113487/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "GE Lighting (10W) GLS LED Bulb A+ Energy Rating 810 Lumens (Pack of 6) 71112",
                    "price": "11.80",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.officestationery.co.uk/images/products/113487-250/ge-lighting-(10w)-gls-led-bulb-a-energy-rating-810.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Discontinued Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.officestationery.co.uk/office-machines/calculators-accessories/calculators/product/GJQ120840J/",
            "html" : cache.load("https://www.officestationery.co.uk/office-machines/calculators-accessories/calculators/product/GJQ120840J/")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Casio DF-120ECO Desk Calculator DF120ECO",
                    "price": "21.90",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.officestationery.co.uk/images/products/GJQ120840J-250/casio-df-120eco-desk-calculator-df120eco.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.officestationery.co.uk/facilities/packaging-supplies/twine-and-string/product/843412/",
            "html" : cache.load("https://www.officestationery.co.uk/facilities/packaging-supplies/twine-and-string/product/843412/")

        }

        it('should return false when product is discontinued', function(done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Thick Sisal Twine (750m) White 166100011",
                    "price": "9.48",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.officestationery.co.uk/images/products/843412-250/thick-sisal-twine-(750m)-white-166100011.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.officestationery.co.uk/office-supplies/craft-supplies/",
            "html" : cache.load("https://www.officestationery.co.uk/office-supplies/craft-supplies/")
        }

        // Change to be the number of products on the page
        var x = 25;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Pentel Paint Marker White Bullet Tip BCX100W';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '3.36',
                    url: 'https://www.officestationery.co.uk/product/pentel-paint-marker-white-bullet-tip-bcx100w-PE02314/',
                    image: 'https://www.officestationery.co.uk/images/products/PE02314-150/Pentel-Paint-Marker-White-Bullet-Tip-BCX100W.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Edding 751 Paint Marker Fine Bullet Tip 1-2mm (Silver) Pack of 10 4-751054';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '24.16',
                    url: 'https://www.officestationery.co.uk/product/edding-751-paint-marker-fine-bullet-tip-1-2mm-silver-pack-of-10-4-751054-122457/',
                    image: 'https://www.officestationery.co.uk/images/products/122457-150/Edding-751-Paint-Marker-Fine-Bullet-Tip-1-2mm-(Sil.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    //  describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : "https://www.officestationery.co.uk/office-supplies/exhibition-and-conference/projectors-ohp/"
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": 200,
    //                 "pages": 1
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.officestationery.co.uk/office-supplies/exhibition-and-conference/projectors-ohp/",
            "html" : cache.load("https://www.officestationery.co.uk/office-supplies/exhibition-and-conference/projectors-ohp/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "projectors ohp",
                    "url":"https://www.officestationery.co.uk/office-supplies/exhibition-and-conference/projectors-ohp/"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
});
