var should = require('should');
var Structure = require('../lib/structure.js')
var cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.opentip.com/product.php?products_id=7854147",
            "html": cache.load('https://www.opentip.com/product.php?products_id=7854147')
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Forum Novelties Candy Cane Walking Stick Adult Costume Accessory",
                    "price": "10.46",
                    "url": "https://www.opentip.com/product.php?products_id=7854147",
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://cdnimages.opentip.com/thumbs/TYK/TYK-FRM-72732-C_280_280.jpg',
                    "country": 'us',
                    "barcode": "721773727320",
                    "sku": "7854147"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    xdescribe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.opentip.com/Automotive/Accessories-c-38922.html",
            "html": cache.load('https://www.opentip.com/Automotive/Accessories-c-38922.html')
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({})
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.opentip.com/Automotive/Accessories-c-38922.html",
            "html": cache.load('https://www.opentip.com/Automotive/Accessories-c-38922.html')
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Accessories",
                    "url" : "https://www.opentip.com/Automotive/Accessories-c-38922.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.opentip.com/search.php?tags=337&page=2",
            "html": cache.load('https://www.opentip.com/search.php?tags=337&page=2')
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'GOGO Swim Pants, Swim Tights, Swimming Pants for Men';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '16.59',
                    url: 'https://www.opentip.com/product.php?products_id=5614635',
                    image: 'https://cdnimages.opentip.com/thumbs/DIVV/DIVV-DK78522_160_160.jpg',
                    country: 'us',
                    sku: '5614635'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'GOGO 6 PCS Rhythmic Gymnastics Ribbons, 4M Dance Streamer Ribbon for Twirling';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '8.99',
                    url: 'https://www.opentip.com/product.php?products_id=3872335',
                    image: 'https://cdnimages.opentip.com/thumbs/AFDH/AFDH-AU13215_160_160.jpg',
                    country: 'us',
                    sku: '3872335'

                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
