var should = require('should');
var Structure = require('../lib/structure.js')
var cache = require('../lib/cache')

describe('www.organizeit.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.organizeit.com/12-hour-coffee-carafe-stainless-steel.asp",
            "html": cache.load('https://www.organizeit.com/12-hour-coffee-carafe-stainless-steel.asp')
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "12 Hour Coffee Carafe - Stainless Steel",
                    "price": "26.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.organizeit.com/images/913-12-hour-coffee-carafe-stainless-steel.jpg?height=300&width=275',
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.organizeit.com/glass-display-box.asp",
            "html": cache.load('https://www.organizeit.com/glass-display-box.asp')
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Glass Display Box",
                    "price": "24.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.organizeit.com/images/glass-display-box.jpg?height=300&width=275',
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : ""
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": "",
    //                 "pages": ""
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.organizeit.com/coffee-makers-and-accessories.asp",
            "html": cache.load('https://www.organizeit.com/coffee-makers-and-accessories.asp')
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Classic Espresso and Cappuccino Machine';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '399.00',
                    url: 'https://www.organizeit.com/classic-espresso-and-cappuccino-machine.asp',
                    image: 'https://www.organizeit.com/images/017-espresso-machine.jpg?width=150&height=180',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Anchor Hocking Glass Creamer';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '3.99',
                    url: 'https://www.organizeit.com/anchor-hocking-glass-creamer.asp',
                    image: 'https://www.organizeit.com/images/anchor-hocking-glass-creamer.jpg?width=150&height=180',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.organizeit.com/coffee-makers-and-accessories.asp",
            "html": cache.load('https://www.organizeit.com/coffee-makers-and-accessories.asp')
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Coffee Makers and Accessories",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
