    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.origins.co.uk/products/15352/skincare/moisturise/moisturiser",
            "html" : cache.load("https://www.origins.co.uk/products/15352/skincare/moisturise/moisturiser")

        }

        // Change to be the number of products on the page
        var x = 32;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Dr. Andrew Weil for Origins™ Mega-Defense Advanced Daily UV Defender SPF 45';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '30.00',
                    url: 'www.origins.co.uk/product/15352/39025/skincare/moisturise/moisturiser/dr-andrew-weil-for-origins/mega-defense-advanced-daily-uv-defender-spf-45',
                    image: 'www.origins.co.uk/media/export/cms/products/200x250/origins_sku_0M5H01_200x250_0.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Balanced Diet™ Lightweight moisture lotion';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '48.00',
                    url: 'www.origins.co.uk/product/15352/11120/skincare/moisturise/moisturiser/balanced-diet/lightweight-moisture-lotion',
                    image: 'www.origins.co.uk/media/export/cms/products/200x250/origins_sku_0EEF01_200x250_0.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

