var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.overclockers.co.uk/corsair-obsidian-750d-airflow-full-tower-case-black-cc-9011078-ww-ca-207-cs.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Corsair Obsidian 750D Airflow Full Tower Case - Black (CC-9011078-WW)",
                    "price": "145.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.overclockers.co.uk/media/image/thumbnail/CA207CS_115678_285x255.jpg',
                    "country": 'uk',
                    "barcode": '0843591057622'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.overclockers.co.uk/cases-and-modding/pc-cases/full-towers"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.overclockers.co.uk/cases-and-modding/pc-cases/full-towers"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Full Tower / Big Tower Computer Cases"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.overclockers.co.uk/cases-and-modding/pc-cases/full-towers"
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Corsair Obsidian 750D Airflow Full Tower Case - Black (CC-9011078-WW)';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '145.99',
                    url: 'https://www.overclockers.co.uk/corsair-obsidian-750d-airflow-full-tower-case-black-cc-9011078-ww-ca-207-cs.html',
                    image: 'https://www.overclockers.co.uk/media/image/thumbnail/CA207CS_115678_105x105.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'In-Win 509 Design Full Tower Tempered Glass Case - Black/Grey';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '159.95',
                    url: 'https://www.overclockers.co.uk/in-win-509-design-full-tower-tempered-glass-case-black-grey-ca-078-iw.html',
                    image: 'https://www.overclockers.co.uk/media/image/thumbnail/CA078IW_149081_105x105.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
