var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.overstock.com/Sports-Toys/Magnolia-Dollhouse-Kit/35729/product.html?refccid=CMIB3ZT2OMDLYZCM3UXOE2YURI&searchidx=5&kwds=&rfmt=age%3A8-11%20Years",
            "html": cache.load("https://www.overstock.com/Sports-Toys/Magnolia-Dollhouse-Kit/35729/product.html?refccid=CMIB3ZT2OMDLYZCM3UXOE2YURI&searchidx=5&kwds=&rfmt=age%3A8-11%20Years")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Magnolia Dollhouse Kit",
                    "price": "74.49",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": "https://ak1.ostkcdn.com/images/products/35729/Magnolia-Dollhouse-Kit-d6a60c80-d964-4f49-9a27-c50971db5881_600.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.overstock.com/Home-Garden/Kitchen-Furniture/1311/cat.html",
            "html": cache.load("https://www.overstock.com/Home-Garden/Kitchen-Furniture/1311/cat.html")
        }

        // Change to be the number of products on the page
        var x = 60;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });
        describe('Search page category', function() {

            var payload = {
                "pageType" : "category",
                "url" : "http://www.overstock.com/Clothing-Shoes/Mens-Sunglasses/3421/cat.html?TID=MENACCSP_SUNGLASSES&products=8028460",
                "html": cache.load("http://www.overstock.com/Clothing-Shoes/Mens-Sunglasses/3421/cat.html?TID=MENACCSP_SUNGLASSES&products=8028460")
            }

            it('should be true when number of items and pages correct', function (done) {

                Structure(payload, function(err, category) {
                    var expected = JSON.stringify({
                        "name": "Men's Sunglasses",
                        "url":"http://www.overstock.com/Clothing-Shoes/Mens-Sunglasses/3421/cat.html?TID=MENACCSP_SUNGLASSES&products=8028460"
                    })
                    JSON.stringify(category).should.equal(expected);
                    done()
                })
            });

        });

        var firstProductName = 'Backyard Discovery Skyfort II All Cedar Swingset';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1455.98',
                    url: 'https://www.overstock.com/Sports-Toys/Backyard-Discovery-Skyfort-II-All-Cedar-Swingset/10754900/product.html?refccid=CMIB3ZT2OMDLYZCM3UXOE2YURI&searchidx=0&kwds=&rfmt=age%3A8-11%20Years',
                    image: 'https://ak1.ostkcdn.com/images/products/10754900/P17808758.jpg?imwidth=320&impolicy=medium',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Super Jumper 2-step Trampoline Ladder';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.84',
                    url: 'https://www.overstock.com/Sports-Toys/Super-Jumper-2-step-Trampoline-Ladder/11868667/product.html?refccid=CMIB3ZT2OMDLYZCM3UXOE2YURI&searchidx=59&kwds=&rfmt=age%3A8-11%20Years',
                    image: 'https://ak1.ostkcdn.com/images/products/11868667/P18767318.jpg?imwidth=320&impolicy=medium',
                    country: 'us'
              })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
