var should = require('should');
var Structure = require('../lib/structure.js')
let cache = require('../lib/cache')


describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.overtons.com/modperl/product/details.cgi?pdesc=Damp-Check-Dome-Moisture-Absorber&i=26666"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Damp Check Dome Moisture Absorber",
                    "price": "12.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn.overtons.com/product/026/26666_L1.jpg?interpolation=lanczos-none&downsize=1200px:1200px',
                    "country": 'us',
                    "description": 'Product Description The 16-oz. Dome Moisture Absorber stops mildew, mold, and musty odors by reducing moisture in the air. The super-dry pellet formula absorbs more excess moisture than flakes. Protects up to 1,000 cubic feet.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.overtons.com/Boating-Marine/Boat-Covers#filters?count=100&do=json&i=1&nav=1&q1=601&q2=601A&q3=Boat+Covers&q4=Boating+Marine&rank=rank2&x1=cat1&x2=cat2&x3=t2&x4=t1&ckey=2eS+iEeMrWyeYN/yourRwQ&cat_depth=2&matched_cat=601A&is_refined=1"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 100,
                    "pages": 149
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.overtons.com/Boating-Marine/Boat-Covers#filters?count=100&do=json&i=1&nav=1&q1=601&q2=601A&q3=Boat+Covers&q4=Boating+Marine&rank=rank2&x1=cat1&x2=cat2&x3=t2&x4=t1&ckey=2eS+iEeMrWyeYN/yourRwQ&cat_depth=2&matched_cat=601A&is_refined=1"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Boat Covers"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.overtons.com/boats-motors-engine-parts/boats",
            "html": cache.load("https://www.overtons.com/boats-motors-engine-parts/boats")
        }

        // Change to be the number of products on the page
        var x = 100;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Damp Check Dome Moisture Absorber';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '12.99',
                    url: 'http://www.overtons.com/modperl/product/details.cgi?pdesc=Damp-Check-Dome-Moisture-Absorber&i=26666',
                    image: 'http://cdn.overtons.com/product/026/26666_L1.jpg?interpolation=lanczos-none&downsize=175px:*',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Covermate Imperial Pro Deck Boat Cover, 20\'5" max. length';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '305.99',
                    url: 'http://www.overtons.com/modperl/product/details.cgi?pdesc=Covermate-Imperial-Pro-Deck-Boat-Cover-205-max-length&i=38934',
                    image: 'http://cdn.overtons.com/product/038/swatches/38934_NVY_1.jpg?interpolation=lanczos-none&downsize=175px:*&composite=0,0|http://cdn.overtons.com/assets/d-banners/gmtn-sale.png',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
