var should = require('should');
var Structure = require('../lib/structure.js')
let cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.ozaroo.com/baby-einstein-take-along-tunes/",
            "html": cache.load("https://www.ozaroo.com/baby-einstein-take-along-tunes/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Baby Einstein Take Along Tunes",
                    "price": "8.88",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'https://cdn6.bigcommerce.com/s-jbxhnf10/images/stencil/1024x1024/products/596/2352/apiyfhlzi__79454.1456160100.jpg?c=2',
                    "country": 'uk',
                    "barcode": '074451307049'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.ozaroo.com/health-personal-care/mens-grooming/shavers",
            "html": cache.load("https://www.ozaroo.com/health-personal-care/mens-grooming/shavers")
        }

        // Change to be the number of products on the page
        var x = 40;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Braun BT3022 Beard/Hair Cordless Rechargeable Trimmer Shaver w Adjustable Length';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '18.59',
                    url: 'https://www.ozaroo.com/braun-bt3022-beard-hair-cordless-rechargeable-trimmer-shaver-w-adjustable-length/',
                    image: 'https://cdn8.bigcommerce.com/s-jbxhnf10/images/stencil/350x350/products/4753/22516/23de5831-3070-4cdd-946d-7dd9f7802bc3__51777.1528370047.jpg?c=2',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Wahl Li Ultimate Men\'s Hair Clipper Cordless Rechargeable with Nose/Ear Trimmer';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '64.26',
                    url: 'https://www.ozaroo.com/wahl-li-ultimate-mens-hair-clipper-cordless-rechargeable-with-nose-ear-trimmer/',
                    image: 'https://cdn8.bigcommerce.com/s-jbxhnf10/images/stencil/350x350/products/3984/17721/apitb6hvm__38243.1527005788.jpg?c=2',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.ozaroo.com/home-tech/",
            "html": cache.load("https://www.ozaroo.com/home-tech/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 99,
                    "pages": 4,
                    "url": payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.ozaroo.com/best-sellers/toys-games/",
            "html": cache.load("https://www.ozaroo.com/best-sellers/toys-games/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Toys & Games",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
