    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.pacsun.com/mens/?country=GB&ICID=0015029"
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Eagle Souvenir Zip Jacket';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '71.18',
                    url: 'http://www.pacsun.com/pacsun/eagle-souvenir-zip-jacket-0201491920032.html',
                    image: 'http://images4.pacsun.com/is/image/pacsunproducts/0201491920032NEW_01_040?$dw-product-list$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'O.B.E.Y. 2 T-Shirt';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.34',
                    url: 'http://www.pacsun.com/obey/o.b.e.y.-2-t-shirt-0097502750189.html',
                    image: 'http://images4.pacsun.com/is/image/pacsunproducts/0097502750189NEW_01_506?$dw-product-list$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

