var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.partmaster.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.partmaster.co.uk/coffee-maker/bosch/tz60001-automatic-coffee-machine-thermos-flask-cleaning-tablets-pack-of-10/product.pl?pid=895479&path=613804,613819,613942",
            "html": cache.load("https://www.partmaster.co.uk/coffee-maker/bosch/tz60001-automatic-coffee-machine-thermos-flask-cleaning-tablets-pack-of-10/product.pl?pid=895479&path=613804,613819,613942")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Bosch Neff Siemens TZ60001 Automatic Coffee Machine & Thermos Flask Cleaning Tablets (Pack Of 10)",
                    "price": "8.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://res.cloudinary.com/dtk4ybaqk/image/private/t_wm_partmaster_420/prod/yk5xca3tn2gado0kngmj.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.partmaster.co.uk/haier/fridge-freezer/defrost-shovel/product.pl?pid=3228205&shop=haier&path=617047",
            "html": cache.load("https://www.partmaster.co.uk/haier/fridge-freezer/defrost-shovel/product.pl?pid=3228205&shop=haier&path=617047")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Defrost Shovel",
                    "price": "5.21",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://res.cloudinary.com/dtk4ybaqk/image/private/t_wm_partmaster_420/v1444388713/prod/hsqf24ilqgdgmqy9abyc.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.partmaster.co.uk/coffee-maker/bosch/catalogue.pl?path=613804,613819,613942",
        "html": cache.load("https://www.partmaster.co.uk/coffee-maker/bosch/catalogue.pl?path=613804,613819,613942")
    }

    // Change to be the number of products on the page
    var x = 12;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Brita Maxtra Plus Cartridges (Pack Of 3)';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '9.99',
                url: 'https://www.partmaster.co.uk/coffee-maker/bosch/maxtra-plus-cartridges-pack-of-3/product.pl?pid=5243504&path=613804,613819,613942',
                image: 'https://res.cloudinary.com/dtk4ybaqk/image/private/t_wm_partmaster_100/v1501602244/prod/c31two1kf9nsugdzkaih.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Bosch Jet Piercer And Funnel Assembly';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '7.31',
                url: 'https://www.partmaster.co.uk/coffee-maker/bosch/jet-piercer-and-funnel-assembly/product.pl?pid=2842033&path=613804,613819,613942',
                image: 'https://res.cloudinary.com/dtk4ybaqk/image/private/t_wm_partmaster_100/prod/heztxnhw6ry85p9uoy78.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.partmaster.co.uk/coffee-maker/bosch/catalogue.pl?path=613804,613819,613942",
            "html": cache.load("https://www.partmaster.co.uk/coffee-maker/bosch/catalogue.pl?path=613804,613819,613942")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Bosch Coffee Maker Parts",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
