    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.parts-express.com/cat/measurement-microphones/2057"
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Dayton Audio EMM-6 Electret Measurement Microphone';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '49.97',
                    url: 'https://www.parts-express.com/dayton-audio-emm-6-electret-measurement-microphone--390-801',
                    image: 'https://www.parts-express.com/data/default/images/catalog/120/390-801_HR_0.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Dayton Audio iMM-6 Calibrated Measurement Microphone for Tablets iPhone iPad and Android';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '14.50',
                    url: 'https://www.parts-express.com/dayton-audio-imm-6-calibrated-measurement-microphone-for-tablets-iphone-ipad-and-android--390-810',
                    image: 'https://www.parts-express.com/data/default/images/catalog/120/390-810_HR_0.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.parts-express.com/cat/1-4-phone-connectors/1502"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 16,
                    "pages": 10
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.parts-express.com/cat/1-4-phone-connectors/1502"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "1/4\" Phone Connectors"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

