var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.partycity.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.partycity.com/girls-blossom-southern-belle-costume-P536448.html?cgid=girls-costumes-1920s-1980s",
            "html": cache.load('https://www.partycity.com/girls-blossom-southern-belle-costume-P536448.html?cgid=girls-costumes-1920s-1980s')
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Girls Blossom Southern Belle Costume",
                    "price": "39.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://partycity.scene7.com/is/image/PartyCity/P536448?fit=constrain,1&wid=500&hei=500&fmt=jpg&qlt=100,1&op_sharpen=0&resMode=sharp2&op_usm=1,1,6,0&iccEmbed=0&printRes=72',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.partycity.com/girls-sandy-olsson-summer-nights-costume---grease-P789570.html?cgid=girls-costumes-1920s-1980s",
            "html": cache.load('https://www.partycity.com/girls-sandy-olsson-summer-nights-costume---grease-P789570.html?cgid=girls-costumes-1920s-1980s')
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Girls Sandy Olsson Summer Nights Costume - Grease",
                    "price": '34.99',
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://partycity.scene7.com/is/image/PartyCity/P789570?fit=constrain,1&wid=500&hei=500&fmt=jpg&qlt=100,1&op_sharpen=0&resMode=sharp2&op_usm=1,1,6,0&iccEmbed=0&printRes=72',
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : "http://www.partycity.com/category/toys/boys/avengers.do"
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": 19,
    //                 "pages": 1
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.partycity.com/girls-costumes-1920s-1980s",
            "html": cache.load('https://www.partycity.com/girls-costumes-1920s-1980s')
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Girls 1920s - 1980s Costumes",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.partycity.com/baby-costumes-bunting",
            "html": cache.load('https://www.partycity.com/baby-costumes-bunting')
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Baby Crochet Cocoon Minnie Mouse Costume';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '4.99',
                    url: 'https://www.partycity.com/baby-crochet-cocoon-minnie-mouse-costume-P635360.html?cgid=baby-costumes',
                    image: 'https://partycity2.scene7.com/is/image/PartyCity/_sq_?$_500x500_$&$product=PartyCity/P635360_full&$hide_Promotions-SALE=0',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Baby Bunting Darling Dragon Costume';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '39.99',
                    url: 'https://www.partycity.com/baby-bunting-darling-dragon-costume-P632696.html?cgid=baby-costumes-boy',
                    image: 'https://partycity1.scene7.com/is/image/PartyCity/_sq_?$_500x500_$&$product=PartyCity/P632696_full',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
