var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.partypacks.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.partypacks.co.uk/veil-with-pink-stars-and-diamantes-pid88039.html",
            "html": cache.load("https://www.partypacks.co.uk/veil-with-pink-stars-and-diamantes-pid88039.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Veil with Pink Stars & Diamantes",
                    "price": "2.95",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.partypacks.co.uk/images/products/product_88039_1_orig.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.partypacks.co.uk/british-union-jack-hearts-bunting-3-3m-pid82249.html",
            "html": cache.load("https://www.partypacks.co.uk/british-union-jack-hearts-bunting-3-3m-pid82249.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "British Union Jack Hearts Bunting - 3.3m",
                    "price": "2.94",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.partypacks.co.uk/images/products/product_82249_1_orig.jpeg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.partypacks.co.uk/hen-party-fancy-dress-cid15004.html",
        "html": cache.load("https://www.partypacks.co.uk/hen-party-fancy-dress-cid15004.html")
    }

    // Change to be the number of products on the page
    var x = 175;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Rose Gold Wedding Tattoos- Pack Of 12FDTARGW';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.74',
                url: 'https://www.partypacks.co.ukrose-gold-wedding-tattoos-pack-of-12-pid104935.html',
                image: 'https://www.partypacks.co.uk/images/products/product_104935_1_listing.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Pale Pink Net TutuFDTTPP';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '5.94',
                url: 'https://www.partypacks.co.ukpale-pink-net-tutu-pid104040.html',
                image: 'https://www.partypacks.co.uk/images/products/product_104040_1_listing.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.partypacks.co.uk/hen-party-fancy-dress-cid15004.html",
            "html": cache.load("https://www.partypacks.co.uk/hen-party-fancy-dress-cid15004.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Hen Party Fancy Dress",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
