var should = require('should');
var Structure = require('../lib/structure.js')

describe('PCRichard', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.pcrichard.com/Dell/Dell-Inspiron-15-5000-Series-15-6inch-Intel-Core-i3-Touchscreen-Notebook-Computer/I55591747SLV.pcrp"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Dell Inspiron 15 5000 Series 15.6\" Intel Core i3 Touchscreen Notebook Computer",
                    "price": "479.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.pcrichard.com/images/product/large/Z_I55591747SLV.jpg',
                    "country": 'us',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.pcrichard.com/Apple/Apple-MacBook-Pro-with-Retina-Display-13-3inch-Intel-Core-i5-Dual-Core-Processor-Notebook-Computer-(early-2015)/MF839LL-A.pcrp"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Apple MacBook Pro with Retina Display 13.3\" Intel Core i5 Dual Core Processor Notebook Computer (early 2015)",
                    "price": "1179.00",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.pcrichard.com/images/product/large/Z_MF839LL-A.jpg',
                    "country": 'us',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.pcrichard.com/category/Computers-Tablets/Computers-Notebooks/_/N-1sjxxkn"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60, // THIS IS THE NUMBER OF PRODUCTS ON THE PAGE YOU ARE LOOKING AT //
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.pcrichard.com/category/Computers-Tablets/Computers-Notebooks/_/N-1sjxxknZd3zn94?Nrpp=60"
        }

        // Change to be the number of products on the page
        var x = 60;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Asus 14.0" Intel Celeron Dual Core Notebook with Windows 10';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '199.99',
                    url: 'http://www.pcrichard.com/Asus/Asus-14-0inch-Intel-Celeron-Dual-Core-Notebook-with-Windows-10/E402SADS01BL.pcrp',
                    image: 'http://www.pcrichard.com/images/product/detail/D_E402SADS01BL.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Acer Aspire R11 11.6" Intel Pentium Quad Core 2-in-1 Notebook - Sky Blue';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '269.99',
                    url: 'http://www.pcrichard.com/Acer/Acer-Aspire-R11-11-6inch-Intel-Pentium-Quad-Core-2-in-1-Notebook-Sky-Blue/R3-131T-P344.pcrp',
                    image: 'http://www.pcrichard.com/images/product/detail/D_R3-131T-P344.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.pcrichard.com/category/Computers-Tablets/Computers-Notebooks/_/N-1sjxxknZd3zn94?Nrpp=60"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Computers & Notebooks"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
