describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.pcworld.co.uk/gbuk/computing-accessories/computer-accessories/pc-speakers/1071_8645_71735_xx_xx/xx-criteria.html"
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'LOGITECH Z333 Multimedia 2.1 PC Speakers';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '44.99',
                    url: 'http://www.pcworld.co.uk/gbuk/computing-accessories/computer-accessories/pc-speakers/logitech-z333-multimedia-2-1-pc-speakers-10024650-pdt.html',
                    image: 'http://brain-images.cdn.dixons.com/0/5/10024650/g_10024650.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'LOGITECH Z623 2.1 PC Speakers';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '139.99',
                    url: 'http://www.pcworld.co.uk/gbuk/computing-accessories/computer-accessories/pc-speakers/logitech-z623-2-1-pc-speakers-10746975-pdt.html',
                    image: 'http://brain-images.cdn.dixons.com/5/7/10746975/g_10746975.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });


