var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.pcworldbusiness.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.pcworldbusiness.co.uk/catalogue/item/N232887W?from=category&heat=title",
            "html": cache.load("https://www.pcworldbusiness.co.uk/catalogue/item/N232887W?from=category&heat=title")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "DYSON Cyclone V10 Animal Cordless Vacuum Cleaner - Purple",
                    "price": "333.32",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://brain-images-ssl.cdn.dixons.com/3/3/10177733/g_10177733.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.pcworldbusiness.co.uk/catalogue/item/P168508P?",
            "html": cache.load("https://www.pcworldbusiness.co.uk/catalogue/item/P168508P?")
        }

        it('should be true when product is out of stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Samsung UE40F5500 5 Series - 40\" LED TV",
                    "price": "20.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn.cnetcontent.com/b0/32/b032e549-ca4d-434d-87d0-07f9b7ffa124.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.pcworldbusiness.co.uk/catalogue/kitchen-domestic-appliances/small-kitchen-appliances/microwaves?from=main-menu",
        "html": cache.load("https://www.pcworldbusiness.co.uk/catalogue/kitchen-domestic-appliances/small-kitchen-appliances/microwaves?from=main-menu")
    }

    // Change to be the number of products on the page
    var x = 15;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'ESSENTIALS C17MW14 Solo Microwave - White';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '44.99',
                url: 'https://www.pcworldbusiness.co.uk/catalogue/item/N067129W?from=category&heat=title',
                image: 'https://brain-images-ssl.cdn.dixons.com/5/9/21729395/l_21729395.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'SAMSUNG MS23F301EAS Solo Microwave - Silver';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '89.99',
                url: 'https://www.pcworldbusiness.co.uk/catalogue/item/N100832W?from=category&heat=title',
                image: 'https://brain-images-ssl.cdn.dixons.com/7/6/10009767/l_10009767.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.pcworldbusiness.co.uk/catalogue/servers-networking/servers?from=main-menu",
            "html": cache.load("https://www.pcworldbusiness.co.uk/catalogue/servers-networking/servers?from=main-menu")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Servers",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


