    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.pet360.com/nav/?cnt=200"
        }

        // Change to be the number of products on the page
        var x = 73;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Heartgard Plus Chewable Tablets for Dogs 51-100 lbs';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '44.66',
                    url: 'https://www.pet360.com/sitesearch?p=R&srid=S1-3IADP&lbc=pet360&w=*&url=http%3a%2f%2fwww.pet360.com%2fproduct%2frx190%2fheartgard-plus-chewable-tablets-for-dogs-51-100-lbs&rk=3&uid=125105040&sid=2&ts=custom&champclick=1&SLIPid=1479912756005&rsc=LzXjwa9cS9JbrnP9&cnt=200&method=and&isort=globalpop&view=grid',
                    image: 'https://pet360.resultspage.com/thumb.php?s=300&f=http%3a%2f%2fwww.petfooddirect.com%2fproductimages%2fRX1-133-01_lg.jpg&time=1479912756',
                    country: ''
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Sentinel Spectrum 26-50lbs';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '60.92',
                    url: 'https://www.pet360.com/sitesearch?p=R&srid=S1-3IADP&lbc=pet360&w=*&url=http%3a%2f%2fwww.pet360.com%2fproduct%2f55434%2fsentinel-spectrum-26-50lbs&rk=200&uid=125105040&sid=2&ts=custom&champclick=1&SLIPid=1479912756005&rsc=bTIjbhnjXVntmlf3&cnt=200&method=and&isort=globalpop&view=grid',
                    image: 'https://pet360.resultspage.com/thumb.php?s=300&f=http%3a%2f%2fwww.petfooddirect.com%2fproductimages%2fRX1-281-05_lg.jpg&time=1479912756',
                    country: ''
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.pet360.com/nav/?cnt=200"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "All Products"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

