describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.petdrugsonline.co.uk/shop-by/dog/food?p=1",
        "html": cache.load("https://www.petdrugsonline.co.uk/shop-by/dog/food?p=1")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Royal Canin Veterinary Diet Gastro Intestinal Low Fat Canine Wet';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '22.91',
                url: 'https://www.petdrugsonline.co.uk/royal-canin-vet-diet-dog-food-gastro-intestinal-low-fat-wet',
                image: 'https://www.petdrugsonline.co.uk/media/catalog/product/cache/7083174b2239b6f4776a1a6f2cddc95e/h/t/httpsimages-petdrugsonline.s3.amazonaws.comimagesroyal-canin-gastro-intestinal-low-fat-canine-wet-2723.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Royal Canin Veterinary Diet Renal Wet Canine';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '24.24',
                url: 'https://www.petdrugsonline.co.uk/royal-canin-vet-diet-dog-food-renal-wet',
                image: 'https://www.petdrugsonline.co.uk/media/catalog/product/cache/7083174b2239b6f4776a1a6f2cddc95e/h/t/httpsimages-petdrugsonline.s3.amazonaws.comimagesroyal-canin-renal-wet-canine-1466.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.petdrugsonline.co.uk/shop-by/dog/food?p=1",
            "html": cache.load("https://www.petdrugsonline.co.uk/shop-by/dog/food?p=1")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Dog Food",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
