describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.petedge.com/zpetedgemain/catalog/home.jsf?area=PMSF-01-13&wec-appid=PEDM_WEBSHOP_TR&wec-locale=en_US&cm=4216",
        "html": cache.load("https://www.petedge.com/zpetedgemain/catalog/home.jsf?area=PMSF-01-13&wec-appid=PEDM_WEBSHOP_TR&wec-locale=en_US&cm=4216")
    }

    // Change to be the number of products on the page
    var x = 12;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Nootie Soft Lilly Passion Shampoos';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '35.99',
                url: 'https://www.petedge.com/zpetedgemain/catalog/productDetail.jsf?area=&sort=RD&wec-appid=PEDM_WEBSHOP_TR&page=C35456610F7C42BFA1CA25328F1F0BBB&itemKey=005056A6CE9C1EE38BA07CF55DCB9A79&show=12&view=grid&wec-locale=en_US',
                image: 'https://www.petedge.com/zpetedgemain/ObjectServer?guid=1e0b5a50b41a11e8b805e7540a0b6572',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'PPP AromaCare Cooling Herbal Mint Shampoo';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '24.89',
                url: 'https://www.petedge.com/zpetedgemain/catalog/productDetail.jsf?area=&sort=RD&wec-appid=PEDM_WEBSHOP_TR&page=C35456610F7C42BFA1CA25328F1F0BBB&itemKey=005056A64D261ED59BA59154811F36EB&show=12&view=grid&wec-locale=en_US',
                image: 'https://www.petedge.com/zpetedgemain/ObjectServer?guid=1ea613b0b41a11e8b29ce7540a0b6572',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.petedge.com/zpetedgemain/catalog/home.jsf?area=PMSF-01-13&wec-appid=PEDM_WEBSHOP_TR&wec-locale=en_US&cm=4216",
            "html": cache.load("https://www.petedge.com/zpetedgemain/catalog/home.jsf?area=PMSF-01-13&wec-appid=PEDM_WEBSHOP_TR&wec-locale=en_US&cm=4216")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "NATURAL",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
