var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.petflow.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.petflow.com/product/acana/acana-heritage-free-run-poultry-formula-dry-dog-food",
            "html": cache.load("https://www.petflow.com/product/acana/acana-heritage-free-run-poultry-formula-dry-dog-food")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Heritage Free Run Poultry Formula Grain Free Dry Dog Food",
                    "price": "57.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.petflow.com/images/default/products/maximal/38363-1451504590.png",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.petflow.com/product/hugglehounds/hugglehounds-holiday-rudolph",
            "html": cache.load("https://www.petflow.com/product/hugglehounds/hugglehounds-holiday-rudolph")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Holiday Knotties Plush Rudolph Dog Toy",
                    "price": "15.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.petflow.com/images/default/products/maximal/39927-1477506084.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.petflow.com/dog-food-dry-food",
        "html": cache.load("https://www.petflow.com/dog-food-dry-food")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Fromm Gold Adult Dry Dog Food';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '52.99',
                url: 'https://www.petflow.com/product/fromm/fromm-gold-adult-dry-dog-food',
                image: 'https://www.petflow.com/images/default/products/200x200/14851-1536591067.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'ACANA Heritage Freshwater Fish Formula Grain Free Dry Dog Food';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '65.99',
                url: 'https://www.petflow.com/product/acana/acana-heritage-freshwater-fish-formula-dry-dog-food',
                image: 'https://www.petflow.com/images/gray.png',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.petflow.com/dog-food-dry-food",
            "html": cache.load("https://www.petflow.com/dog-food-dry-food")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Dry Food",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
