    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.petfooddirect.com/nav/Dog-Toys"
        }

        // Change to be the number of products on the page
        var x = 40;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Kong Rubber Dog Chew Toy';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '7.19',
                    url: 'https://www.petfooddirect.com/sitesearch?p=R&srid=S1-4DFWP&lbc=petfooddirect&w=*&url=http%3a%2f%2fwww.petfooddirect.com%2fproduct%2f822%2fkong-rubber-dog-chew-toy&rk=1&uid=334650754&sid=2&ts=custom&champclick=1&rsc=ZswonZfo4D%3aU6tRB&method=and&isort=globalpop&view=grid',
                    image: 'http://petfooddirect.resultspage.com/thumb.php?s=300&f=http%3a%2f%2fwww.petfooddirect.com%2fproductimages%2f1015601_lg.jpg&time=1469533465',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'DoggieNation Houston Texans Plush Dog Toy';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '12.33',
                    url: 'https://www.petfooddirect.com/sitesearch?p=R&srid=S1-4DFWP&lbc=petfooddirect&w=*&url=http%3a%2f%2fwww.petfooddirect.com%2fproduct%2f56649%2fdoggienation-houston-texans-plush-dog-toy&rk=40&uid=334650754&sid=2&ts=custom&champclick=1&rsc=RCRGUknnO%3akVjIsx&method=and&isort=globalpop&view=grid',
                    image: 'http://petfooddirect.resultspage.com/thumb.php?s=300&f=http%3a%2f%2fwww.petfooddirect.com%2fproductimages%2f212973762_lg.jpg&time=1469533465',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.petfooddirect.com/nav/Dog-Toys"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 40,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.petfooddirect.com/nav/Dog-Toys"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

